step.script:
// Wrapper for the JSON object that we'll construct
var hl7JSON = {
	"message_type" : "SIU",
	"content" : {}
};

// Save the wrapper in a channelMap variable for later usage
channelMap.put("hl7JSON", JSON.stringify(hl7JSON));

