				if (pv1Seg['PV1.6']['PV1.6.4']['PV1.6.4.2'].toString() != "") { pid[index].PV1[indexPV1].priorPatientLocation.facility.universalId =  pv1Seg['PV1.6']['PV1.6.4']['PV1.6.4.2']; }
