// PID iteration, in order to grab related OBX's
if (msg['PID'].length() > 0) {

	// PID is a required to have a DG1, but if the PID was blank and was removed from the JSON, just create a new one to add the DG1 into
	if (!pid) {
		pid = [{}];
	}

	var index = 0;
	for each (seg in msg.PID) {

		// DG1 (PID)
		var dg1Group = getSegmentsAfter(msg,seg,'DG1',false);
		if (dg1Group.length > 0 && pid) {
