entry.string:
// RGS
var rgs;
if (msg['RGS'].length() > 0) {
	rgs = [];
	var index = 0;
	for each (seg in msg.RGS) {
		rgs[index] = {};

		// RGS.1, setId
		if (seg['RGS.1']['RGS.1.1'].toString() != "") {rgs[index].setId = seg['RGS.1']['RGS.1.1']; }

		// RGS.2, segmentActionCode
		if (seg['RGS.2']['RGS.2.1'].toString() != "") {rgs[index].segmentActionCode = seg['RGS.2']['RGS.2.1']; }

		// RGS.3, resourceGroupId
		rgs[index].resourceGroupId = {};
		if (seg['RGS.3']['RGS.3.1'].toString() != "") {rgs[index].resourceGroupId.id = seg['RGS.3']['RGS.3.1']; }
		if (seg['RGS.3']['RGS.3.2'].toString() != "") {rgs[index].resourceGroupId.text = seg['RGS.3']['RGS.3.2']; }
		if (seg['RGS.3']['RGS.3.3'].toString() != "") {rgs[index].resourceGroupId.codingSystem = seg['RGS.3']['RGS.3.3']; }
		if (seg['RGS.3']['RGS.3.4'].toString() != "") {rgs[index].resourceGroupId.altId = seg['RGS.3']['RGS.3.4']; }
		if (seg['RGS.3']['RGS.3.5'].toString() != "") {rgs[index].resourceGroupId.altText = seg['RGS.3']['RGS.3.5']; }
		if (seg['RGS.3']['RGS.3.6'].toString() != "") {rgs[index].resourceGroupId.altCodingSystem = seg['RGS.3']['RGS.3.6']; }
		if (Object.keys(rgs[index].resourceGroupId).length == 0) {
			delete rgs[index].resourceGroupId;
		}

		if (Object.keys(rgs[index]).length == 0) {
			delete rgs[index];
		} else {
			index++;
		}
	}
}

