step.script:
// Pull out the RGS JSON to embed the AIS segments in
var rgs = JSON.parse($('hl7JSON')).content.RGS;

// RGS is a required to have a AIS, but if the RGS was blank and was removed from the JSON, just create a new one to add the AIS into
if (!rgs) {
	rgs = [{}];
}

// RGS
if (msg['RGS'].length() > 0) {
	var index = 0;
	for each (seg in msg.RGS) {

		// AIS (RGS)
		var aisGroup = getSegmentsAfter(msg,seg,'AIS',false);
		if (aisGroup.length > 0 && rgs) {
			rgs[index].AIS = [];
			var indexAIS = 0;
			for each (aisSeg in aisGroup) {
				rgs[index].AIS[indexAIS] = {};

				// AIS.1, setId
				if (aisSeg['AIS.1']['AIS.1.1'].toString() != "") { rgs[index].AIS[indexAIS].setId = aisSeg['AIS.1']['AIS.1.1']; }

				// AIS.2, segmentActionCode
				if (aisSeg['AIS.2']['AIS.2.1'].toString() != "") { rgs[index].AIS[indexAIS].segmentActionCode = aisSeg['AIS.2']['AIS.2.1']; }

				// AIS.3, universalServiceId
				rgs[index].AIS[indexAIS].universalServiceId = {};
				if (aisSeg['AIS.3']['AIS.3.1'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.id = aisSeg['AIS.3']['AIS.3.1']; }
				if (aisSeg['AIS.3']['AIS.3.2'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.text = aisSeg['AIS.3']['AIS.3.2']; }
				if (aisSeg['AIS.3']['AIS.3.3'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.codingSystem = aisSeg['AIS.3']['AIS.3.3']; }
				if (aisSeg['AIS.3']['AIS.3.4'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.altId = aisSeg['AIS.3']['AIS.3.4']; }
				if (aisSeg['AIS.3']['AIS.3.5'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.altText = aisSeg['AIS.3']['AIS.3.5']; }
				if (aisSeg['AIS.3']['AIS.3.6'].toString() != "") { rgs[index].AIS[indexAIS].universalServiceId.altCodingSystem = aisSeg['AIS.3']['AIS.3.6']; }
				if (Object.keys(rgs[index].AIS[indexAIS].universalServiceId).length == 0) {
					delete rgs[index].AIS[indexAIS].universalServiceId;
				}

				// AIS.4, startDateTime
				if (aisSeg['AIS.4']['AIS.4.1'].toString() != "") { rgs[index].AIS[indexAIS].startDateTime = aisSeg['AIS.4']['AIS.4.1']; }

				// AIS.5, startDateTimeOffset
				if (aisSeg['AIS.5']['AIS.5.1'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffset = aisSeg['AIS.5']['AIS.5.1']; }

				// AIS.6, startDateTimeOffsetUnits
				rgs[index].AIS[indexAIS].startDateTimeOffsetUnits = {};
				if (aisSeg['AIS.6']['AIS.6.1'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.id = aisSeg['AIS.6']['AIS.6.1']; }
				if (aisSeg['AIS.6']['AIS.6.2'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.text = aisSeg['AIS.6']['AIS.6.2']; }
				if (aisSeg['AIS.6']['AIS.6.3'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.codingSystem = aisSeg['AIS.6']['AIS.6.3']; }
				if (aisSeg['AIS.6']['AIS.6.4'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.altId = aisSeg['AIS.6']['AIS.6.4']; }
				if (aisSeg['AIS.6']['AIS.6.5'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.altText = aisSeg['AIS.6']['AIS.6.5']; }
				if (aisSeg['AIS.6']['AIS.6.6'].toString() != "") { rgs[index].AIS[indexAIS].startDateTimeOffsetUnits.altCodingSystem = aisSeg['AIS.6']['AIS.6.6']; }
				if (Object.keys(rgs[index].AIS[indexAIS].startDateTimeOffsetUnits).length == 0) {
					delete rgs[index].AIS[indexAIS].startDateTimeOffsetUnits;
				}

				// AIS.7, duration
				if (aisSeg['AIS.7']['AIS.7.1'].toString() != "") { rgs[index].AIS[indexAIS].duration = aisSeg['AIS.7']['AIS.7.1']; }

				// AIS.8, durationUnits
				rgs[index].AIS[indexAIS].durationUnits = {};
				if (aisSeg['AIS.8']['AIS.8.1'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.id = aisSeg['AIS.8']['AIS.8.1']; }
				if (aisSeg['AIS.8']['AIS.8.2'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.text = aisSeg['AIS.8']['AIS.8.2']; }
				if (aisSeg['AIS.8']['AIS.8.3'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.codingSystem = aisSeg['AIS.8']['AIS.8.3']; }
				if (aisSeg['AIS.8']['AIS.8.4'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.altId = aisSeg['AIS.8']['AIS.8.4']; }
				if (aisSeg['AIS.8']['AIS.8.5'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.altText = aisSeg['AIS.8']['AIS.8.5']; }
				if (aisSeg['AIS.8']['AIS.8.6'].toString() != "") { rgs[index].AIS[indexAIS].durationUnits.altCodingSystem = aisSeg['AIS.8']['AIS.8.6']; }
				if (Object.keys(rgs[index].AIS[indexAIS].durationUnits).length == 0) {
					delete rgs[index].AIS[indexAIS].durationUnits;
				}

				// AIS.9, allowSubstitutionCode
				if (aisSeg['AIS.9']['AIS.9.1'].toString() != "") { rgs[index].AIS[indexAIS].allowSubstitutionCode = aisSeg['AIS.9']['AIS.9.1']; }

				// AIS.10, fillerStatusCode
				rgs[index].AIS[indexAIS].fillerStatusCode = {};
				if (aisSeg['AIS.10']['AIS.10.1'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.id = aisSeg['AIS.10']['AIS.10.1']; }
				if (aisSeg['AIS.10']['AIS.10.2'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.text = aisSeg['AIS.10']['AIS.10.2']; }
				if (aisSeg['AIS.10']['AIS.10.3'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.codingSystem = aisSeg['AIS.10']['AIS.10.3']; }
				if (aisSeg['AIS.10']['AIS.10.4'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.altId = aisSeg['AIS.10']['AIS.10.4']; }
				if (aisSeg['AIS.10']['AIS.10.5'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.altText = aisSeg['AIS.10']['AIS.10.5']; }
				if (aisSeg['AIS.10']['AIS.10.6'].toString() != "") { rgs[index].AIS[indexAIS].fillerStatusCode.altCodingSystem = aisSeg['AIS.10']['AIS.10.6']; }
				if (Object.keys(rgs[index].AIS[indexAIS].fillerStatusCode).length == 0) {
					delete rgs[index].AIS[indexAIS].fillerStatusCode;
				}

				// NTE (AIS)
				var nteGroup = getSegmentsAfter(msg,aisSeg,'NTE',true);
				if (nteGroup.length > 0) {
					rgs[index].AIS[indexAIS].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						rgs[index].AIS[indexAIS].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { rgs[index].AIS[indexAIS].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { rgs[index].AIS[indexAIS].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						rgs[index].AIS[indexAIS].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								rgs[index].AIS[indexAIS].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (rgs[index].AIS[indexAIS].NTE[indexNTE].comment.length == 0) {
							delete rgs[index].AIS[indexAIS].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(rgs[index].AIS[indexAIS].NTE[indexNTE]).length == 0) {
							delete rgs[index].AIS[indexAIS].NTE[indexNTE];
						} else {
							indexNTE++;
						}
					}
				}

				// clean up AIS and increment index
				if (Object.keys(rgs[index].AIS[indexAIS]).length == 0) {
					delete rgs[index].AIS[indexAIS];
				} else {
					indexAIS++;
				}
			} 

			if (rgs[index].AIS[0] == null) {
				delete rgs[index].AIS;
			}
		}

		index++;
	}
}

