				// AIG.3, resourceId
				rgs[index].AIG[indexAIG].resourceId = {};
				if (aigSeg['AIG.3']['AIG.3.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.id = aigSeg['AIG.3']['AIG.3.1'];}
				if (aigSeg['AIG.3']['AIG.3.2'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.text = aigSeg['AIG.3']['AIG.3.2'];}
				if (aigSeg['AIG.3']['AIG.3.3'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.codingSystem = aigSeg['AIG.3']['AIG.3.3'];}
				if (aigSeg['AIG.3']['AIG.3.4'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altId = aigSeg['AIG.3']['AIG.3.4'];}
				if (aigSeg['AIG.3']['AIG.3.5'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altText = aigSeg['AIG.3']['AIG.3.5'];}
				if (aigSeg['AIG.3']['AIG.3.6'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altCodingSystem = aigSeg['AIG.3']['AIG.3.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].resourceId).length == 0) { 
					delete rgs[index].AIG[indexAIG].resourceId; 
				}

				// AIG.4, resourceType
				rgs[index].AIG[indexAIG].resourceType = {};
				if (aigSeg['AIG.4']['AIG.4.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.id = aigSeg['AIG.4']['AIG.4.1'];}
				if (aigSeg['AIG.4']['AIG.4.2'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.text = aigSeg['AIG.4']['AIG.4.2'];}
				if (aigSeg['AIG.4']['AIG.4.3'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.codingSystem = aigSeg['AIG.4']['AIG.4.3'];}
				if (aigSeg['AIG.4']['AIG.4.4'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altId = aigSeg['AIG.4']['AIG.4.4'];}
				if (aigSeg['AIG.4']['AIG.4.5'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altText = aigSeg['AIG.4']['AIG.4.5'];}
				if (aigSeg['AIG.4']['AIG.4.6'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altCodingSystem = aigSeg['AIG.4']['AIG.4.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].resourceType).length == 0) { 
					delete rgs[index].AIG[indexAIG].resourceType; 
				}

				// AIG.5, resourceGroup
				rgs[index].AIG[indexAIG].resourceGroup = [];
				for each (field in aigSeg['AIG.5']) {
				    var block = {};
				    if (field['AIG.5.1'].toString() != "") { block.id = field['AIG.5.1']; }
					if (field['AIG.5.2'].toString() != "") { block.text = field['AIG.5.2']; }
					if (field['AIG.5.3'].toString() != "") { block.codingSystem = field['AIG.5.3']; }
					if (field['AIG.5.4'].toString() != "") { block.altId = field['AIG.5.4']; }
					if (field['AIG.5.5'].toString() != "") { block.altText = field['AIG.5.5']; }
					if (field['AIG.5.6'].toString() != "") { block.altCodingSystem = field['AIG.5.6']; }
				    if (Object.keys(block).length > 0) {
				        rgs[index].AIG[indexAIG].resourceGroup.push(block);
				    }
				}
				if (rgs[index].AIG[indexAIG].resourceGroup.length == 0) {
					delete rgs[index].AIG[indexAIG].resourceGroup;
				}

				// AIG.6, resourceQuantity
				if (aigSeg['AIG.6']['AIG.6.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantity = aigSeg['AIG.6']['AIG.6.1']; }

				// AIG.7, resourceQuantityUnits
				rgs[index].AIG[indexAIG].resourceQuantityUnits = {};
				if (aigSeg['AIG.7']['AIG.7.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.id = aigSeg['AIG.7']['AIG.7.1'];}
				if (aigSeg['AIG.7']['AIG.7.2'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.text = aigSeg['AIG.7']['AIG.7.2'];}
				if (aigSeg['AIG.7']['AIG.7.3'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.codingSystem = aigSeg['AIG.7']['AIG.7.3'];}
				if (aigSeg['AIG.7']['AIG.7.4'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.altId = aigSeg['AIG.7']['AIG.7.4'];}
				if (aigSeg['AIG.7']['AIG.7.5'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.altText = aigSeg['AIG.7']['AIG.7.5'];}
				if (aigSeg['AIG.7']['AIG.7.6'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantityUnits.altCodingSystem = aigSeg['AIG.7']['AIG.7.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].resourceQuantityUnits).length == 0) { 
					delete rgs[index].AIG[indexAIG].resourceQuantityUnits; 
				}

				// AIG.8, startDateTime
				if (aigSeg['AIG.8']['AIG.8.1'].toString() != "") { rgs[index].AIG[indexAIG].startDateTime = aigSeg['AIG.8']['AIG.8.1']; }

				// AIG.9, startDateTimeOffset
				if (aigSeg['AIG.9']['AIG.9.1'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffset = aigSeg['AIG.9']['AIG.9.1']; }

				// AIG.10, startDateTimeOffsetUnits
				rgs[index].AIG[indexAIG].startDateTimeOffsetUnits = {};
				if (aigSeg['AIG.10']['AIG.10.1'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.id = aigSeg['AIG.10']['AIG.10.1'];}
				if (aigSeg['AIG.10']['AIG.10.2'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.text = aigSeg['AIG.10']['AIG.10.2'];}
				if (aigSeg['AIG.10']['AIG.10.3'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.codingSystem = aigSeg['AIG.10']['AIG.10.3'];}
				if (aigSeg['AIG.10']['AIG.10.4'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.altId = aigSeg['AIG.10']['AIG.10.4'];}
				if (aigSeg['AIG.10']['AIG.10.5'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.altText = aigSeg['AIG.10']['AIG.10.5'];}
				if (aigSeg['AIG.10']['AIG.10.6'].toString() != "") { rgs[index].AIG[indexAIG].startDateTimeOffsetUnits.altCodingSystem = aigSeg['AIG.10']['AIG.10.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].startDateTimeOffsetUnits).length == 0) { 
					delete rgs[index].AIG[indexAIG].startDateTimeOffsetUnits; 
				}

				// AIG.11, duration
				if (aigSeg['AIG.11']['AIG.11.1'].toString() != "") { rgs[index].AIG[indexAIG].duration = aigSeg['AIG.11']['AIG.11.1']; }

				// AIG.12, durationUnits
				rgs[index].AIG[indexAIG].durationUnits = {};
				if (aigSeg['AIG.12']['AIG.12.1'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.id = aigSeg['AIG.12']['AIG.12.1'];}
				if (aigSeg['AIG.12']['AIG.12.2'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.text = aigSeg['AIG.12']['AIG.12.2'];}
				if (aigSeg['AIG.12']['AIG.12.3'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.codingSystem = aigSeg['AIG.12']['AIG.12.3'];}
				if (aigSeg['AIG.12']['AIG.12.4'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.altId = aigSeg['AIG.12']['AIG.12.4'];}
				if (aigSeg['AIG.12']['AIG.12.5'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.altText = aigSeg['AIG.12']['AIG.12.5'];}
				if (aigSeg['AIG.12']['AIG.12.6'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.altCodingSystem = aigSeg['AIG.12']['AIG.12.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].durationUnits).length == 0) { 
					delete rgs[index].AIG[indexAIG].durationUnits; 
				}

				// AIG.13, allowSubstitutionCode
				if (aigSeg['AIG.13']['AIG.13.1'].toString() != "") { rgs[index].AIG[indexAIG].allowSubstitutionCode = aigSeg['AIG.13']['AIG.13.1']; }

				// AIG.14, fillerStatusCode
				rgs[index].AIG[indexAIG].fillerStatusCode = {};
				if (aigSeg['AIG.14']['AIG.14.1'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.id = aigSeg['AIG.14']['AIG.14.1'];}
				if (aigSeg['AIG.14']['AIG.14.2'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.text = aigSeg['AIG.14']['AIG.14.2'];}
				if (aigSeg['AIG.14']['AIG.14.3'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.codingSystem = aigSeg['AIG.14']['AIG.14.3'];}
				if (aigSeg['AIG.14']['AIG.14.4'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altId = aigSeg['AIG.14']['AIG.14.4'];}
				if (aigSeg['AIG.14']['AIG.14.5'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altText = aigSeg['AIG.14']['AIG.14.5'];}
				if (aigSeg['AIG.14']['AIG.14.6'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altCodingSystem = aigSeg['AIG.14']['AIG.14.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].fillerStatusCode).length == 0) { 
					delete rgs[index].AIG[indexAIG].fillerStatusCode; 
				}

				// NTE (AIG)
				var nteGroup = getSegmentsAfter(msg,aigSeg,'NTE',true);
				if (nteGroup.length > 0) {
					rgs[index].AIG[indexAIG].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						rgs[index].AIG[indexAIG].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { rgs[index].AIG[indexAIG].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { rgs[index].AIG[indexAIG].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						rgs[index].AIG[indexAIG].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								rgs[index].AIG[indexAIG].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (rgs[index].AIG[indexAIG].NTE[indexNTE].comment.length == 0) {
							delete rgs[index].AIG[indexAIG].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(rgs[index].AIG[indexAIG].NTE[indexNTE]).length == 0) {
							delete rgs[index].AIG[indexAIG].NTE[indexNTE];
						} else {
							indexNTE++;
						}
					}
				}

				// clean up AIG and increment index
				if (Object.keys(rgs[index].AIG[indexAIG]).length == 0) {
					delete rgs[index].AIG[indexAIG];
				} else {
					indexAIG++;
				}
			}

			if (rgs[index].AIG[0] == null) {
				delete rgs[index].AIG;
			}
		} 

		index++;
	}
}

// Optional: save a copy of the completed RGS segment for reference later
//channelMap.put("rgs", rgs); // uncomment to save

// If it has content, add the RGS segment to the hl7JSON object
if (rgs && Object.keys(rgs).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.RGS = rgs;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

entry.string:
// Pull out the RGS JSON to embed the AIG segments in
var rgs = JSON.parse($('hl7JSON')).content.RGS;

// RGS is a required to have a AIG, but if the RGS was blank and was removed from the JSON, just create a new one to add the AIG into
if (!rgs) {
	rgs = [{}];
}

// RGS
if (msg['RGS'].length() > 0) {
	var index = 0;
	for each (seg in msg.RGS) {

		// AIG (RGS)
		var aigGroup = getSegmentsAfter(msg,seg,'AIG',false);
		if (aigGroup.length > 0 && rgs) {
			rgs[index].AIG = [];
			var indexAIG = 0;
			for each (aigSeg in aigGroup) {
				rgs[index].AIG[indexAIG] = {};

				// AIG.1, setId
				if (aigSeg['AIG.1']['AIG.1.1'].toString() != "") { rgs[index].AIG[indexAIG].setId = aigSeg['AIG.1']['AIG.1.1']; }

				// AIG.2, aigSegmentActionCode
				if (aigSeg['AIG.2']['AIG.2.1'].toString() != "") { rgs[index].AIG[indexAIG].aigSegmentActionCode = aigSeg['AIG.2']['AIG.2.1']; }

				// AIG.3, resourceId
				rgs[index].AIG[indexAIG].resourceId = {};
				if (aigSeg['AIG.3']['AIG.3.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.id = aigSeg['AIG.3']['AIG.3.1'];}
				if (aigSeg['AIG.3']['AIG.3.2'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.text = aigSeg['AIG.3']['AIG.3.2'];}
				if (aigSeg['AIG.3']['AIG.3.3'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.codingSystem = aigSeg['AIG.3']['AIG.3.3'];}
				if (aigSeg['AIG.3']['AIG.3.4'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altId = aigSeg['AIG.3']['AIG.3.4'];}
				if (aigSeg['AIG.3']['AIG.3.5'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altText = aigSeg['AIG.3']['AIG.3.5'];}
				if (aigSeg['AIG.3']['AIG.3.6'].toString() != "") { rgs[index].AIG[indexAIG].resourceId.altCodingSystem = aigSeg['AIG.3']['AIG.3.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].resourceId).length == 0) { 
					delete rgs[index].AIG[indexAIG].resourceId; 
				}

				// AIG.4, resourceType
				rgs[index].AIG[indexAIG].resourceType = {};
				if (aigSeg['AIG.4']['AIG.4.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.id = aigSeg['AIG.4']['AIG.4.1'];}
				if (aigSeg['AIG.4']['AIG.4.2'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.text = aigSeg['AIG.4']['AIG.4.2'];}
				if (aigSeg['AIG.4']['AIG.4.3'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.codingSystem = aigSeg['AIG.4']['AIG.4.3'];}
				if (aigSeg['AIG.4']['AIG.4.4'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altId = aigSeg['AIG.4']['AIG.4.4'];}
				if (aigSeg['AIG.4']['AIG.4.5'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altText = aigSeg['AIG.4']['AIG.4.5'];}
				if (aigSeg['AIG.4']['AIG.4.6'].toString() != "") { rgs[index].AIG[indexAIG].resourceType.altCodingSystem = aigSeg['AIG.4']['AIG.4.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].resourceType).length == 0) { 
					delete rgs[index].AIG[indexAIG].resourceType; 
				}

				// AIG.5, resourceGroup
				rgs[index].AIG[indexAIG].resourceGroup = [];
				for each (field in aigSeg['AIG.5']) {
				    var block = {};
				    if (field['AIG.5.1'].toString() != "") { block.id = field['AIG.5.1']; }
					if (field['AIG.5.2'].toString() != "") { block.text = field['AIG.5.2']; }
					if (field['AIG.5.3'].toString() != "") { block.codingSystem = field['AIG.5.3']; }
					if (field['AIG.5.4'].toString() != "") { block.altId = field['AIG.5.4']; }
					if (field['AIG.5.5'].toString() != "") { block.altText = field['AIG.5.5']; }
					if (field['AIG.5.6'].toString() != "") { block.altCodingSystem = field['AIG.5.6']; }
				    if (Object.keys(block).length > 0) {
				        rgs[index].AIG[indexAIG].resourceGroup.push(block);
				    }
				}
				if (rgs[index].AIG[indexAIG].resourceGroup.length == 0) {
					delete rgs[index].AIG[indexAIG].resourceGroup;
				}

				// AIG.6, resourceQuantity
				if (aigSeg['AIG.6']['AIG.6.1'].toString() != "") { rgs[index].AIG[indexAIG].resourceQuantity = aigSeg['AIG.6']['AIG.6.1']; }

