				// AIG.11, duration
				if (aigSeg['AIG.11']['AIG.11.1'].toString() != "") { rgs[index].AIG[indexAIG].duration = aigSeg['AIG.11']['AIG.11.1']; }

				// AIG.12, durationUnits
				rgs[index].AIG[indexAIG].durationUnits = {};
				if (aigSeg['AIG.12']['AIG.12.1'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.id = aigSeg['AIG.12']['AIG.12.1'];}
				if (aigSeg['AIG.12']['AIG.12.2'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.text = aigSeg['AIG.12']['AIG.12.2'];}
				if (aigSeg['AIG.12']['AIG.12.3'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.codingSystem = aigSeg['AIG.12']['AIG.12.3'];}
				if (aigSeg['AIG.12']['AIG.12.4'].toString() != "") { rgs[index].AIG[indexAIG].durationUnits.altId = aigSeg['AIG.12']['AIG.12.4'];}
