				if (aigSeg['AIG.14']['AIG.14.3'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.codingSystem = aigSeg['AIG.14']['AIG.14.3'];}
				if (aigSeg['AIG.14']['AIG.14.4'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altId = aigSeg['AIG.14']['AIG.14.4'];}
				if (aigSeg['AIG.14']['AIG.14.5'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altText = aigSeg['AIG.14']['AIG.14.5'];}
				if (aigSeg['AIG.14']['AIG.14.6'].toString() != "") { rgs[index].AIG[indexAIG].fillerStatusCode.altCodingSystem = aigSeg['AIG.14']['AIG.14.6'];}
				if (Object.keys(rgs[index].AIG[indexAIG].fillerStatusCode).length == 0) { 
					delete rgs[index].AIG[indexAIG].fillerStatusCode; 
				}

				// NTE (AIG)
				var nteGroup = getSegmentsAfter(msg,aigSeg,'NTE',true);
				if (nteGroup.length > 0) {
					rgs[index].AIG[indexAIG].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						rgs[index].AIG[indexAIG].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { rgs[index].AIG[indexAIG].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { rgs[index].AIG[indexAIG].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						rgs[index].AIG[indexAIG].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								rgs[index].AIG[indexAIG].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (rgs[index].AIG[indexAIG].NTE[indexNTE].comment.length == 0) {
							delete rgs[index].AIG[indexAIG].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(rgs[index].AIG[indexAIG].NTE[indexNTE]).length == 0) {
							delete rgs[index].AIG[indexAIG].NTE[indexNTE];
						} else {
							indexNTE++;
						}
					}
				}

				// clean up AIG and increment index
				if (Object.keys(rgs[index].AIG[indexAIG]).length == 0) {
					delete rgs[index].AIG[indexAIG];
				} else {
					indexAIG++;
				}
			}

			if (rgs[index].AIG[0] == null) {
				delete rgs[index].AIG;
			}
		} 

		index++;
	}
}

// Optional: save a copy of the completed RGS segment for reference later
//channelMap.put("rgs", rgs); // uncomment to save

// If it has content, add the RGS segment to the hl7JSON object
if (rgs && Object.keys(rgs).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.RGS = rgs;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the RGS JSON to embed the AIL segments in
var rgs = JSON.parse($('hl7JSON')).content.RGS;

// RGS is a required to have a AIL, but if the RGS was blank and was removed from the JSON, just create a new one to add the AIL into
if (!rgs) {
	rgs = [{}];
}

// RGS
if (msg['RGS'].length() > 0) {
	var index = 0;
	for each (seg in msg.RGS) {

		// AIL (RGS)
		var ailGroup = getSegmentsAfter(msg,seg,'AIL',false);
		if (ailGroup.length > 0 && rgs) {
			rgs[index].AIL = [];
			var indexAIL = 0;
			for each (ailSeg in ailGroup) {
				rgs[index].AIL[indexAIL] = {};

				// AIL.1, setId
				if (ailSeg['AIL.1']['AIL.1.1'].toString() != "") { rgs[index].AIL[indexAIL].setId = ailSeg['AIL.1']['AIL.1.1']; }

				// AIL.2, ailSegmentActionCode
				if (ailSeg['AIL.2']['AIL.2.1'].toString() != "") { rgs[index].AIL[indexAIL].ailSegmentActionCode = ailSeg['AIL.2']['AIL.2.1']; }

				// AIL.3, locationResourceId
				rgs[index].AIL[indexAIL].locationResourceId = {};
				if (ailSeg['AIL.3']['AIL.3.1'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.pointOfCare = ailSeg['AIL.3']['AIL.3.1']; }
				if (ailSeg['AIL.3']['AIL.3.2'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.room = ailSeg['AIL.3']['AIL.3.2']; }
				if (ailSeg['AIL.3']['AIL.3.3'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.bed = ailSeg['AIL.3']['AIL.3.3']; }
				rgs[index].AIL[indexAIL].locationResourceId.facility = {};
				if (ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.1'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.facility.nameSpaceId =  ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.1']; }
				if (ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.2'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.facility.universalId =  ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.2']; }
				if (ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.3'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.facility.universalIdType =  ailSeg['AIL.3']['AIL.3.4']['AIL.3.4.3']; }
				if (Object.keys(rgs[index].AIL[indexAIL].locationResourceId.facility).length == 0) {
					delete rgs[index].AIL[indexAIL].locationResourceId.facility;
				}
				if (ailSeg['AIL.3']['AIL.3.5'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.locationStatus = ailSeg['AIL.3']['AIL.3.5']; }
				if (ailSeg['AIL.3']['AIL.3.6'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.personLocationType = ailSeg['AIL.3']['AIL.3.6']; }
				if (ailSeg['AIL.3']['AIL.3.7'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.building = ailSeg['AIL.3']['AIL.3.7']; }
				if (ailSeg['AIL.3']['AIL.3.8'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.floor = ailSeg['AIL.3']['AIL.3.8']; }
				if (ailSeg['AIL.3']['AIL.3.9'].toString() != "") { rgs[index].AIL[indexAIL].locationResourceId.locationDescription = ailSeg['AIL.3']['AIL.3.9']; }
				if (Object.keys(rgs[index].AIL[indexAIL].locationResourceId).length == 0) {
					delete rgs[index].AIL[indexAIL].locationResourceId;
				}

				// AIL.4, locationType
				rgs[index].AIL[indexAIL].locationType = {};
				if (ailSeg['AIL.4']['AIL.4.1'].toString() != "") { rgs[index].AIL[indexAIL].locationType.id = ailSeg['AIL.4']['AIL.4.1']; }
				if (ailSeg['AIL.4']['AIL.4.2'].toString() != "") { rgs[index].AIL[indexAIL].locationType.text = ailSeg['AIL.4']['AIL.4.2']; }
				if (ailSeg['AIL.4']['AIL.4.3'].toString() != "") { rgs[index].AIL[indexAIL].locationType.codingSystem = ailSeg['AIL.4']['AIL.4.3']; }
				if (ailSeg['AIL.4']['AIL.4.4'].toString() != "") { rgs[index].AIL[indexAIL].locationType.altId = ailSeg['AIL.4']['AIL.4.4']; }
				if (ailSeg['AIL.4']['AIL.4.5'].toString() != "") { rgs[index].AIL[indexAIL].locationType.altText = ailSeg['AIL.4']['AIL.4.5']; }
				if (ailSeg['AIL.4']['AIL.4.6'].toString() != "") { rgs[index].AIL[indexAIL].locationType.altCodingSystem = ailSeg['AIL.4']['AIL.4.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].locationType).length == 0) {
					delete rgs[index].AIL[indexAIL].locationType;
				}

				// AIL.5, locationGroup
				rgs[index].AIL[indexAIL].locationGroup = {};
				if (ailSeg['AIL.5']['AIL.5.1'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.id = ailSeg['AIL.5']['AIL.5.1']; }
				if (ailSeg['AIL.5']['AIL.5.2'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.text = ailSeg['AIL.5']['AIL.5.2']; }
				if (ailSeg['AIL.5']['AIL.5.3'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.codingSystem = ailSeg['AIL.5']['AIL.5.3']; }
				if (ailSeg['AIL.5']['AIL.5.4'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.altId = ailSeg['AIL.5']['AIL.5.4']; }
				if (ailSeg['AIL.5']['AIL.5.5'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.altText = ailSeg['AIL.5']['AIL.5.5']; }
				if (ailSeg['AIL.5']['AIL.5.6'].toString() != "") { rgs[index].AIL[indexAIL].locationGroup.altCodingSystem = ailSeg['AIL.5']['AIL.5.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].locationGroup).length == 0) {
					delete rgs[index].AIL[indexAIL].locationGroup;
				}

				// AIL.6, startDateTime
				if (ailSeg['AIL.6']['AIL.6.1'].toString() != "") { rgs[index].AIL[indexAIL].startDateTime = ailSeg['AIL.6']['AIL.6.1']; }

				// AIL.7, startDateTimeOffset
				if (ailSeg['AIL.7']['AIL.7.1'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffset = ailSeg['AIL.7']['AIL.7.1']; }

				// AIL.8, startDateTimeOffsetUnits
				rgs[index].AIL[indexAIL].startDateTimeOffsetUnits = {};
				if (ailSeg['AIL.8']['AIL.8.1'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.id = ailSeg['AIL.8']['AIL.8.1']; }
				if (ailSeg['AIL.8']['AIL.8.2'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.text = ailSeg['AIL.8']['AIL.8.2']; }
				if (ailSeg['AIL.8']['AIL.8.3'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.codingSystem = ailSeg['AIL.8']['AIL.8.3']; }
				if (ailSeg['AIL.8']['AIL.8.4'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.altId = ailSeg['AIL.8']['AIL.8.4']; }
				if (ailSeg['AIL.8']['AIL.8.5'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.altText = ailSeg['AIL.8']['AIL.8.5']; }
				if (ailSeg['AIL.8']['AIL.8.6'].toString() != "") { rgs[index].AIL[indexAIL].startDateTimeOffsetUnits.altCodingSystem = ailSeg['AIL.8']['AIL.8.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].startDateTimeOffsetUnits).length == 0) {
					delete rgs[index].AIL[indexAIL].startDateTimeOffsetUnits;
				}

				// AIL.9, duration
				if (ailSeg['AIL.9']['AIL.9.1'].toString() != "") { rgs[index].AIL[indexAIL].duration = ailSeg['AIL.9']['AIL.9.1']; }

				// AIL.10, durationUnits
				rgs[index].AIL[indexAIL].durationUnits = {};
				if (ailSeg['AIL.10']['AIL.10.1'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.id = ailSeg['AIL.10']['AIL.10.1']; }
				if (ailSeg['AIL.10']['AIL.10.2'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.text = ailSeg['AIL.10']['AIL.10.2']; }
				if (ailSeg['AIL.10']['AIL.10.3'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.codingSystem = ailSeg['AIL.10']['AIL.10.3']; }
				if (ailSeg['AIL.10']['AIL.10.4'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altId = ailSeg['AIL.10']['AIL.10.4']; }
				if (ailSeg['AIL.10']['AIL.10.5'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altText = ailSeg['AIL.10']['AIL.10.5']; }
				if (ailSeg['AIL.10']['AIL.10.6'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altCodingSystem = ailSeg['AIL.10']['AIL.10.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].durationUnits).length == 0) {
					delete rgs[index].AIL[indexAIL].durationUnits;
				}

				// AIL.11, allowSubstitutionCode
				if (ailSeg['AIL.11']['AIL.11.1'].toString() != "") { rgs[index].AIL[indexAIL].allowSubstitutionCode = ailSeg['AIL.11']['AIL.11.1']; }

				// AIL.12, fillerStatusCode
				rgs[index].AIL[indexAIL].fillerStatusCode = {};
				if (ailSeg['AIL.12']['AIL.12.1'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.id = ailSeg['AIL.12']['AIL.12.1']; }
				if (ailSeg['AIL.12']['AIL.12.2'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.text = ailSeg['AIL.12']['AIL.12.2']; }
				if (ailSeg['AIL.12']['AIL.12.3'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.codingSystem = ailSeg['AIL.12']['AIL.12.3']; }
