				// AIL.10, durationUnits
				rgs[index].AIL[indexAIL].durationUnits = {};
				if (ailSeg['AIL.10']['AIL.10.1'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.id = ailSeg['AIL.10']['AIL.10.1']; }
				if (ailSeg['AIL.10']['AIL.10.2'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.text = ailSeg['AIL.10']['AIL.10.2']; }
				if (ailSeg['AIL.10']['AIL.10.3'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.codingSystem = ailSeg['AIL.10']['AIL.10.3']; }
				if (ailSeg['AIL.10']['AIL.10.4'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altId = ailSeg['AIL.10']['AIL.10.4']; }
				if (ailSeg['AIL.10']['AIL.10.5'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altText = ailSeg['AIL.10']['AIL.10.5']; }
				if (ailSeg['AIL.10']['AIL.10.6'].toString() != "") { rgs[index].AIL[indexAIL].durationUnits.altCodingSystem = ailSeg['AIL.10']['AIL.10.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].durationUnits).length == 0) {
					delete rgs[index].AIL[indexAIL].durationUnits;
				}

				// AIL.11, allowSubstitutionCode
				if (ailSeg['AIL.11']['AIL.11.1'].toString() != "") { rgs[index].AIL[indexAIL].allowSubstitutionCode = ailSeg['AIL.11']['AIL.11.1']; }

				// AIL.12, fillerStatusCode
				rgs[index].AIL[indexAIL].fillerStatusCode = {};
				if (ailSeg['AIL.12']['AIL.12.1'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.id = ailSeg['AIL.12']['AIL.12.1']; }
				if (ailSeg['AIL.12']['AIL.12.2'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.text = ailSeg['AIL.12']['AIL.12.2']; }
				if (ailSeg['AIL.12']['AIL.12.3'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.codingSystem = ailSeg['AIL.12']['AIL.12.3']; }
				if (ailSeg['AIL.12']['AIL.12.4'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.altId = ailSeg['AIL.12']['AIL.12.4']; }
				if (ailSeg['AIL.12']['AIL.12.5'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.altText = ailSeg['AIL.12']['AIL.12.5']; }
				if (ailSeg['AIL.12']['AIL.12.6'].toString() != "") { rgs[index].AIL[indexAIL].fillerStatusCode.altCodingSystem = ailSeg['AIL.12']['AIL.12.6']; }
				if (Object.keys(rgs[index].AIL[indexAIL].fillerStatusCode).length == 0) {
					delete rgs[index].AIL[indexAIL].fillerStatusCode;
				}

				// NTE (AIL)
				var nteGroup = getSegmentsAfter(msg,seg,'NTE',true);
				if (nteGroup.length > 0) {
					rgs[index].AIL[indexAIL].nte = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						rgs[index].AIL[indexAIL].nte[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { rgs[index].AIL[indexAIL].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { rgs[index].AIL[indexAIL].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						rgs[index].AIL[indexAIL].nte[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								rgs[index].AIL[indexAIL].nte[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (rgs[index].AIL[indexAIL].NTE[indexNTE].comment.length == 0) {
							delete rgs[index].AIL[indexAIL].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(rgs[index].AIL[indexAIL].NTE[indexNTE]).length == 0) {
							delete rgs[index].AIL[indexAIL].NTE[indexNTE];
						} else {
							indexNTE++;
						}
					}
				}

				// clean up AIL and increment index
				if (Object.keys(rgs[index].AIL[indexAIL]).length == 0) {
					delete rgs[index].AIL[indexAIL];
				} else {
					indexAIL++;
				}
			} 

			if (rgs[index].AIL[0] == null) {
				delete rgs[index].AIL;
			}
		} 

		index++;
	}
}

// Optional: save a copy of the completed RGS segment for reference later
//channelMap.put("rgs", rgs); // uncomment to save

// If it has content, add the RGS segment to the hl7JSON object
if (rgs && Object.keys(rgs).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.RGS = rgs;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the RGS JSON to embed the AIP segments in
var rgs = JSON.parse($('hl7JSON')).content.RGS;

// RGS is a required to have a AIP, but if the RGS was blank and was removed from the JSON, just create a new one to add the AIP into
if (!rgs) {
	rgs = [{}];
}

// RGS
if (msg['RGS'].length() > 0) {
	var index = 0;
	for each (seg in msg.RGS) {

		// AIP (RGS)
		var aipGroup = getSegmentsAfter(msg,seg,'AIP',false);
		if (aipGroup.length > 0 && rgs) {
			rgs[index].AIP = [];
			var indexAIP = 0;
			for each (aipSeg in aipGroup) {
				rgs[index].AIP[indexAIP] = {};

				// AIP.1, setId
				if (aipSeg['AIP.1']['AIP.1.1'].toString() != "") { rgs[index].AIP[indexAIP].setId = aipSeg['AIP.1']['AIP.1.1']; }

				// AIP.2, aipSegmentActionCode
				if (aipSeg['AIP.2']['AIP.2.1'].toString() != "") { rgs[index].AIP[indexAIP].aipSegmentActionCode = aipSeg['AIP.2']['AIP.2.1']; }

				// AIP.3, personnelResourceId
				rgs[index].AIP[indexAIP].personnelResourceId = {};
				if (aipSeg['AIP.3']['AIP.3.1'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.pointOfCare = aipSeg['AIP.3']['AIP.3.1']; }
				if (aipSeg['AIP.3']['AIP.3.2'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.room = aipSeg['AIP.3']['AIP.3.2']; }
				if (aipSeg['AIP.3']['AIP.3.3'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.bed = aipSeg['AIP.3']['AIP.3.3']; }
				rgs[index].AIP[indexAIP].personnelResourceId.facility = {};
				if (aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.1'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.facility.nameSpaceId =  aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.1']; }
				if (aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.2'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.facility.universalId =  aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.2']; }
				if (aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.3'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.facility.universalIdType =  aipSeg['AIP.3']['AIP.3.4']['AIP.3.4.3']; }
				if (Object.keys(rgs[index].AIP[indexAIP].personnelResourceId.facility).length == 0) {
					delete rgs[index].AIP[indexAIP].personnelResourceId.facility;
				}
				if (aipSeg['AIP.3']['AIP.3.5'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.locationStatus = aipSeg['AIP.3']['AIP.3.5']; }
				if (aipSeg['AIP.3']['AIP.3.6'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.personLocationType = aipSeg['AIP.3']['AIP.3.6']; }
				if (aipSeg['AIP.3']['AIP.3.7'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.building = aipSeg['AIP.3']['AIP.3.7']; }
				if (aipSeg['AIP.3']['AIP.3.8'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.floor = aipSeg['AIP.3']['AIP.3.8']; }
				if (aipSeg['AIP.3']['AIP.3.9'].toString() != "") { rgs[index].AIP[indexAIP].personnelResourceId.locationDescription = aipSeg['AIP.3']['AIP.3.9']; }
				if (Object.keys(rgs[index].AIP[indexAIP].personnelResourceId).length == 0) {
					delete rgs[index].AIP[indexAIP].personnelResourceId;
				}

				// AIP.4, resourceRole
				rgs[index].AIP[indexAIP].resourceRole = {};
				if (aipSeg['AIP.4']['AIP.4.1'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.id = aipSeg['AIP.4']['AIP.4.1']; }
				if (aipSeg['AIP.4']['AIP.4.2'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.text = aipSeg['AIP.4']['AIP.4.2']; }
				if (aipSeg['AIP.4']['AIP.4.3'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.codingSystem = aipSeg['AIP.4']['AIP.4.3']; }
				if (aipSeg['AIP.4']['AIP.4.4'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.altId = aipSeg['AIP.4']['AIP.4.4']; }
				if (aipSeg['AIP.4']['AIP.4.5'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.altText = aipSeg['AIP.4']['AIP.4.5']; }
				if (aipSeg['AIP.4']['AIP.4.6'].toString() != "") { rgs[index].AIP[indexAIP].resourceRole.altCodingSystem = aipSeg['AIP.4']['AIP.4.6']; }
				if (Object.keys(rgs[index].AIP[indexAIP].resourceRole).length == 0) {
					delete rgs[index].AIP[indexAIP].resourceRole;
				}

				// AIP.5, resourceGroup
				rgs[index].AIP[indexAIP].resourceGroup = {};
				if (aipSeg['AIP.5']['AIP.5.1'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.id = aipSeg['AIP.5']['AIP.5.1']; }
				if (aipSeg['AIP.5']['AIP.5.2'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.text = aipSeg['AIP.5']['AIP.5.2']; }
				if (aipSeg['AIP.5']['AIP.5.3'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.codingSystem = aipSeg['AIP.5']['AIP.5.3']; }
				if (aipSeg['AIP.5']['AIP.5.4'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.altId = aipSeg['AIP.5']['AIP.5.4']; }
				if (aipSeg['AIP.5']['AIP.5.5'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.altText = aipSeg['AIP.5']['AIP.5.5']; }
				if (aipSeg['AIP.5']['AIP.5.6'].toString() != "") { rgs[index].AIP[indexAIP].resourceGroup.altCodingSystem = aipSeg['AIP.5']['AIP.5.6']; }
				if (Object.keys(rgs[index].AIP[indexAIP].resourceGroup).length == 0) {
					delete rgs[index].AIP[indexAIP].resourceGroup;
				}

				// AIP.6, startDateTime
				if (aipSeg['AIP.6']['AIP.6.1'].toString() != "") { rgs[index].AIP[indexAIP].startDateTime = aipSeg['AIP.6']['AIP.6.1']; }

				// AIP.7, startDateTimeOffset
				if (aipSeg['AIP.7']['AIP.7.1'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffset = aipSeg['AIP.7']['AIP.7.1']; }

				// AIP.8, startDateTimeOffsetUnits
				rgs[index].AIP[indexAIP].startDateTimeOffsetUnits = {};
				if (aipSeg['AIP.8']['AIP.8.1'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.id = aipSeg['AIP.8']['AIP.8.1']; }
				if (aipSeg['AIP.8']['AIP.8.2'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.text = aipSeg['AIP.8']['AIP.8.2']; }
				if (aipSeg['AIP.8']['AIP.8.3'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.codingSystem = aipSeg['AIP.8']['AIP.8.3']; }
				if (aipSeg['AIP.8']['AIP.8.4'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.altId = aipSeg['AIP.8']['AIP.8.4']; }
				if (aipSeg['AIP.8']['AIP.8.5'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.altText = aipSeg['AIP.8']['AIP.8.5']; }
				if (aipSeg['AIP.8']['AIP.8.6'].toString() != "") { rgs[index].AIP[indexAIP].startDateTimeOffsetUnits.altCodingSystem = aipSeg['AIP.8']['AIP.8.6']; }
				if (Object.keys(rgs[index].AIP[indexAIP].startDateTimeOffsetUnits).length == 0) {
					delete rgs[index].AIP[indexAIP].startDateTimeOffsetUnits;
				}

				// AIP.9, duration
				if (aipSeg['AIP.9']['AIP.9.1'].toString() != "") { rgs[index].AIP[indexAIP].duration = aipSeg['AIP.9']['AIP.9.1']; }

				// AIP.10, durationUnits
				rgs[index].AIP[indexAIP].durationUnits = {};
				if (aipSeg['AIP.10']['AIP.10.1'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.id = aipSeg['AIP.10']['AIP.10.1']; }
				if (aipSeg['AIP.10']['AIP.10.2'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.text = aipSeg['AIP.10']['AIP.10.2']; }
				if (aipSeg['AIP.10']['AIP.10.3'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.codingSystem = aipSeg['AIP.10']['AIP.10.3']; }
				if (aipSeg['AIP.10']['AIP.10.4'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.altId = aipSeg['AIP.10']['AIP.10.4']; }
				if (aipSeg['AIP.10']['AIP.10.5'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.altText = aipSeg['AIP.10']['AIP.10.5']; }
				if (aipSeg['AIP.10']['AIP.10.6'].toString() != "") { rgs[index].AIP[indexAIP].durationUnits.altCodingSystem = aipSeg['AIP.10']['AIP.10.6']; }
				if (Object.keys(rgs[index].AIP[indexAIP].durationUnits).length == 0) {
					delete rgs[index].AIP[indexAIP].durationUnits;
				}

				// AIP.11, allowSubstitutionCode
				if (aipSeg['AIP.11']['AIP.11.1'].toString() != "") { rgs[index].AIP[indexAIP].allowSubstitutionCode = aipSeg['AIP.11']['AIP.11.1']; }

				// AIP.12, fillerStatusCode
				rgs[index].AIP[indexAIP].fillerStatusCode = {};
				if (aipSeg['AIP.12']['AIP.12.1'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.id = aipSeg['AIP.12']['AIP.12.1']; }
				if (aipSeg['AIP.12']['AIP.12.2'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.text = aipSeg['AIP.12']['AIP.12.2']; }
				if (aipSeg['AIP.12']['AIP.12.3'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.codingSystem = aipSeg['AIP.12']['AIP.12.3']; }
				if (aipSeg['AIP.12']['AIP.12.4'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.altId = aipSeg['AIP.12']['AIP.12.4']; }
				if (aipSeg['AIP.12']['AIP.12.5'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.altText = aipSeg['AIP.12']['AIP.12.5']; }
				if (aipSeg['AIP.12']['AIP.12.6'].toString() != "") { rgs[index].AIP[indexAIP].fillerStatusCode.altCodingSystem = aipSeg['AIP.12']['AIP.12.6']; }
				if (Object.keys(rgs[index].AIP[indexAIP].fillerStatusCode).length == 0) {
					delete rgs[index].AIP[indexAIP].fillerStatusCode;
				}

				// NTE (AIP)
				var nteGroup = getSegmentsAfter(msg,seg,'NTE',true);
				if (nteGroup.length > 0) {
					rgs[index].AIP[indexAIP].nte = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						rgs[index].AIP[indexAIP].nte[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { rgs[index].AIP[indexAIP].nte[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { rgs[index].AIP[indexAIP].nte[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						rgs[index].AIP[indexAIP].nte[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								rgs[index].AIP[indexAIP].nte[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (rgs[index].AIP[indexAIP].NTE[indexNTE].comment.length == 0) {
							delete rgs[index].AIP[indexAIP].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(rgs[index].AIP[indexAIP].NTE[indexNTE]).length == 0) {
							delete rgs[index].AIP[indexAIP].NTE[indexNTE];
						} else {
							indexNTE++;
						}

					}

				}

				// clean up AIP and increment index
				if (Object.keys(rgs[index].AIP[indexAIP]).length == 0) {
					delete rgs[index].AIP[indexAIP];
				} else {
					indexAIP++;
				}

			}
			if (rgs[index].AIP[0] == null) {
				delete rgs[index].AIP;
			}
		}

		index++;
	}
}

